import React, { useState, useRef, useEffect } from 'react';
import { FiSend, FiDownload, FiPhone, FiMail, FiMapPin, FiMessageSquare, FiGlobe, FiClock, FiMessageCircle, FiX, FiChevronDown } from 'react-icons/fi';
import { FaWhatsapp } from 'react-icons/fa';
import { Button } from './ui/Button';
import { Dialog } from './ui/Dialog';
import axios from 'axios';

const API_ENDPOINT = "https://crud-backend-a70z.onrender.com/api/products";

// Message types
const MessageType = {
  USER: "user",
  BOT: "bot",
  PRODUCT: "product",
  CONTACT: "contact",
  ENQUIRY: "enquiry",
  CATALOG: "catalog",
  SUPPORT: "support",
  ERROR: "error",
  LOADING: "loading"
};

// Available languages
const LANGUAGES = {
  en: { label: 'English', flag: 'ðŸ‡ºðŸ‡¸' },
  es: { label: 'EspaÃ±ol', flag: 'ðŸ‡ªðŸ‡¸' },
  hi: { label: 'à¤¹à¤¿à¤‚à¤¦à¥€', flag: 'ðŸ‡®ðŸ‡³' }
};

// Languages
const translations = {
  en: {
    welcome: "Hello! How can I assist you today? You can type:\n- 'Products' to see our data sorting solutions\n- 'Contact' for our details\n- 'Enquiry' to submit a request\n- 'Catalog' to download our catalog\n- 'Support' to speak with an agent",
    product: "Our Products",
    contact: "Contact Information",
    enquiry: "Submit Enquiry",
    support: "Support Options",
    catalog: "Download Catalog",
    thankYou: "Thank you for your enquiry! Our team will reach out to you shortly.",
    supportHours: "Support Hours: Monday - Friday, 9 AM - 6 PM EST",
    loading: "Loading products...",
    error: "Sorry, there was an error loading the products. Please try again.",
    price: "Price",
    viewMore: "View More",
    noProducts: "No products found.",
    currency: "USD"
  },
  es: {
    welcome: "Â¡Hola! Â¿CÃ³mo puedo ayudarte hoy? Puedes escribir:\n- 'Productos' para ver nuestras soluciones\n- 'Contacto' para nuestros detalles\n- 'Consulta' para enviar una solicitud\n- 'CatÃ¡logo' para descargar nuestro catÃ¡logo\n- 'Soporte' para hablar con un agente",
    product: "Nuestros Productos",
    contact: "InformaciÃ³n de Contacto",
    enquiry: "Enviar Consulta",
    support: "Opciones de Soporte",
    catalog: "Descargar CatÃ¡logo",
    thankYou: "Â¡Gracias por tu consulta! Nuestro equipo se pondrÃ¡ en contacto contigo pronto.",
    supportHours: "Horario de Soporte: Lunes - Viernes, 9 AM - 6 PM EST",
    loading: "Cargando productos...",
    error: "Lo siento, hubo un error al cargar los productos. Por favor, intÃ©ntalo de nuevo.",
    price: "Precio",
    viewMore: "Ver MÃ¡s",
    noProducts: "No se encontraron productos.",
    currency: "USD"
  },
  hi: {
    welcome: "à¤¨à¤®à¤¸à¥à¤¤à¥‡! à¤®à¥ˆà¤‚ à¤†à¤ªà¤•à¥€ à¤•à¥ˆà¤¸à¥‡ à¤®à¤¦à¤¦ à¤•à¤° à¤¸à¤•à¤¤à¤¾ à¤¹à¥‚à¤‚? à¤†à¤ª à¤Ÿà¤¾à¤‡à¤ª à¤•à¤° à¤¸à¤•à¤¤à¥‡ à¤¹à¥ˆà¤‚:\n- 'Products' à¤‰à¤¤à¥à¤ªà¤¾à¤¦ à¤¦à¥‡à¤–à¤¨à¥‡ à¤•à¥‡ à¤²à¤¿à¤\n- 'Contact' à¤¸à¤‚à¤ªà¤°à¥à¤• à¤œà¤¾à¤¨à¤•à¤¾à¤°à¥€ à¤•à¥‡ à¤²à¤¿à¤\n- 'Enquiry' à¤ªà¥‚à¤›à¤¤à¤¾à¤› à¤•à¥‡ à¤²à¤¿à¤\n- 'Catalog' à¤•à¥ˆà¤Ÿà¤²à¥‰à¤— à¤¡à¤¾à¤‰à¤¨à¤²à¥‹à¤¡ à¤•à¥‡ à¤²à¤¿à¤\n- 'Support' à¤¸à¤¹à¤¾à¤¯à¤¤à¤¾ à¤•à¥‡ à¤²à¤¿à¤",
    product: "à¤¹à¤®à¤¾à¤°à¥‡ à¤‰à¤¤à¥à¤ªà¤¾à¤¦",
    contact: "à¤¸à¤‚à¤ªà¤°à¥à¤• à¤œà¤¾à¤¨à¤•à¤¾à¤°à¥€",
    enquiry: "à¤ªà¥‚à¤›à¤¤à¤¾à¤› à¤•à¤°à¥‡à¤‚",
    support: "à¤¸à¤¹à¤¾à¤¯à¤¤à¤¾ à¤µà¤¿à¤•à¤²à¥à¤ª",
    catalog: "à¤•à¥ˆà¤Ÿà¤²à¥‰à¤— à¤¡à¤¾à¤‰à¤¨à¤²à¥‹à¤¡ à¤•à¤°à¥‡à¤‚",
    thankYou: "à¤†à¤ªà¤•à¥€ à¤ªà¥‚à¤›à¤¤à¤¾à¤› à¤•à¥‡ à¤²à¤¿à¤ à¤§à¤¨à¥à¤¯à¤µà¤¾à¤¦! à¤¹à¤®à¤¾à¤°à¥€ à¤Ÿà¥€à¤® à¤œà¤²à¥à¤¦ à¤¹à¥€ à¤†à¤ªà¤¸à¥‡ à¤¸à¤‚à¤ªà¤°à¥à¤• à¤•à¤°à¥‡à¤—à¥€à¥¤",
    supportHours: "à¤¸à¤¹à¤¾à¤¯à¤¤à¤¾ à¤¸à¤®à¤¯: à¤¸à¥‹à¤®à¤µà¤¾à¤° - à¤¶à¥à¤•à¥à¤°à¤µà¤¾à¤°, à¤¸à¥à¤¬à¤¹ 9 à¤¬à¤œà¥‡ - à¤¶à¤¾à¤® 6 à¤¬à¤œà¥‡ EST",
    loading: "à¤‰à¤¤à¥à¤ªà¤¾à¤¦ à¤²à¥‹à¤¡ à¤¹à¥‹ à¤°à¤¹à¥‡ à¤¹à¥ˆà¤‚...",
    error: "à¤•à¥à¤·à¤®à¤¾ à¤•à¤°à¥‡à¤‚, à¤‰à¤¤à¥à¤ªà¤¾à¤¦ à¤²à¥‹à¤¡ à¤•à¤°à¤¨à¥‡ à¤®à¥‡à¤‚ à¤à¤• à¤¤à¥à¤°à¥à¤Ÿà¤¿ à¤¹à¥à¤ˆà¥¤ à¤•à¥ƒà¤ªà¤¯à¤¾ à¤ªà¥à¤¨à¤ƒ à¤ªà¥à¤°à¤¯à¤¾à¤¸ à¤•à¤°à¥‡à¤‚à¥¤",
    price: "à¤®à¥‚à¤²à¥à¤¯",
    viewMore: "à¤”à¤° à¤¦à¥‡à¤–à¥‡à¤‚",
    noProducts: "à¤•à¥‹à¤ˆ à¤‰à¤¤à¥à¤ªà¤¾à¤¦ à¤¨à¤¹à¥€à¤‚ à¤®à¤¿à¤²à¤¾à¥¤",
    currency: "USD"
  }
};

const ChatBot = () => {
  const [isOpen, setIsOpen] = useState(false);
  const [isLargeMode, setIsLargeMode] = useState(false);
  const [messages, setMessages] = useState([]);
  const [inputValue, setInputValue] = useState('');
  const [showEnquiryForm, setShowEnquiryForm] = useState(false);
  const [language, setLanguage] = useState('en');
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    phone: '',
    message: ''
  });
  const messagesEndRef = useRef(null);
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState(null);
  const [products, setProducts] = useState([]);
  const [showLanguageMenu, setShowLanguageMenu] = useState(false);
  const [isLoadingProducts, setIsLoadingProducts] = useState(false);
  const languageMenuRef = useRef(null);

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  };

  useEffect(() => {
    // Detect user's language
    const userLang = navigator.language.split('-')[0];
    if (translations[userLang]) {
      setLanguage(userLang);
    }

    // Initial welcome message
    setMessages([{
      type: 'bot',
      content: translations[language].welcome
    }]);
  }, []);

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  // Close language menu when clicking outside
  useEffect(() => {
    const handleClickOutside = (event) => {
      if (languageMenuRef.current && !languageMenuRef.current.contains(event.target)) {
        setShowLanguageMenu(false);
      }
    };

    document.addEventListener('mousedown', handleClickOutside);
    return () => document.removeEventListener('mousedown', handleClickOutside);
  }, []);

  const handleInputChange = (e) => {
    setInputValue(e.target.value);
  };

  const handleFormChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  // Function to fetch products from API
  const fetchProducts = async () => {
    setIsLoading(true);
    setError(null);
    try {
      const response = await axios.get(API_ENDPOINT);
      setProducts(response.data);
    } catch (err) {
      setError(err.message);
      console.error('Error fetching products:', err);
    } finally {
      setIsLoading(false);
    }
  };

  // Function to format price in Indian currency
  const formatPrice = (price) => {
    return new Intl.NumberFormat('en-IN', {
      style: 'currency',
      currency: 'INR',
      maximumFractionDigits: 0
    }).format(price);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const userMessage = inputValue.trim().toLowerCase();
    
    if (!userMessage) return;

    setMessages(prev => [...prev, { type: MessageType.USER, content: inputValue }]);
    setInputValue('');

    switch (userMessage) {
      case 'product':
      case 'products':
      case 'productos':
      case 'à¤‰à¤¤à¥à¤ªà¤¾à¤¦':
        await handleProductRequest();
        break;
      case 'contact':
      case 'contacto':
      case 'à¤¸à¤‚à¤ªà¤°à¥à¤•':
        handleContactRequest();
        break;
      case 'enquiry':
      case 'consulta':
      case 'à¤ªà¥‚à¤›à¤¤à¤¾à¤›':
        setShowEnquiryForm(true);
        break;
      case 'catalog':
      case 'catÃ¡logo':
      case 'à¤•à¥ˆà¤Ÿà¤²à¥‰à¤—':
        handleCatalogRequest();
        break;
      case 'support':
      case 'soporte':
      case 'à¤¸à¤¹à¤¾à¤¯à¤¤à¤¾':
        handleSupportRequest();
        break;
      default:
        setMessages(prev => [...prev, {
          type: MessageType.BOT,
          content: translations[language].welcome
        }]);
    }
  };

  // Enhanced product request handler
  const handleProductRequest = async () => {
    setIsLoadingProducts(true);
    setMessages(prev => [...prev, {
      type: MessageType.LOADING,
      content: translations[language].loading
    }]);

    try {
      const response = await axios.get(API_ENDPOINT);
      const products = response.data;

      if (!products || products.length === 0) {
        setMessages(prev => [...prev.slice(0, -1), {
          type: MessageType.BOT,
          content: translations[language].noProducts
        }]);
        return;
      }

      setMessages(prev => [...prev.slice(0, -1), {
        type: MessageType.PRODUCT,
        content: (
          <div className="space-y-4">
            <h3 className="text-[#00ff00] font-semibold mb-4">Here are our SwanSorter solutions:</h3>
            <div className="grid grid-cols-1 gap-4">
              {products.map((product) => (
                <div 
                  key={product._id}
                  className="bg-[#000000] border border-[#00ff00]/30 rounded-lg p-4 hover:border-[#00ff00] hover:shadow-[0_0_10px_#00ff00] transition-all duration-300"
                >
                  <h4 className="text-[#00ff00] font-semibold mb-2">{product.name}</h4>
                  <p className="text-[#00ff00]/80 text-sm mb-3">{product.description}</p>
                  <div className="flex justify-between items-center">
                    <span className="text-[#00ff00] font-bold">{formatPrice(product.price)}</span>
                    <Button
                      variant="outline"
                      size="sm"
                      onClick={() => window.open(product.url || '#', '_blank')}
                      className="text-[#00ff00] border-[#00ff00] hover:bg-[#00ff00]/10 hover:border-[#00ff00] hover:shadow-[0_0_5px_#00ff00] transition-all duration-300"
                    >
                      Learn More
                    </Button>
                  </div>
                </div>
              ))}
            </div>
          </div>
        )
      }]);
    } catch (error) {
      console.error('Error fetching products:', error);
      setMessages(prev => [...prev.slice(0, -1), {
        type: MessageType.ERROR,
        content: translations[language].error
      }]);
    } finally {
      setIsLoadingProducts(false);
    }
  };

  const handleContactRequest = () => {
    setMessages(prev => [...prev, {
      type: 'bot',
      content: (
        <div className="space-y-4 animate-fade-in">
          <h3 className="font-bold">{translations[language].contact}:</h3>
          <div className="space-y-2">
            <p className="flex items-center gap-2">
              <FiPhone className="text-light-primary dark:text-dark-primary" />
              <span>+1 234 567 890</span>
            </p>
            <p className="flex items-center gap-2">
              <FiMail className="text-light-primary dark:text-dark-primary" />
              <span>support@example.com</span>
            </p>
            <p className="flex items-center gap-2">
              <FiMapPin className="text-light-primary dark:text-dark-primary" />
              <span>123 Business Street, City, Country</span>
            </p>
            <button className="mt-4 bg-light-primary dark:bg-dark-primary text-white px-4 py-2 rounded-lg flex items-center gap-2 hover:opacity-90 transition-opacity">
              <FiPhone /> Call Us
            </button>
          </div>
        </div>
      )
    }]);
  };

  const handleCatalogRequest = () => {
    setMessages(prev => [...prev, {
      type: 'bot',
      content: (
        <div className="space-y-4 animate-fade-in">
          <h3 className="font-bold">{translations[language].catalog}</h3>
          <button 
            className="flex items-center gap-2 bg-light-primary dark:bg-dark-primary text-white px-4 py-2 rounded-lg hover:opacity-90 transition-opacity"
            onClick={() => window.open('/catalog.pdf', '_blank')}
          >
            <FiDownload />
            Download Now
          </button>
        </div>
      )
    }]);
  };

  const handleSupportRequest = () => {
    setMessages(prev => [...prev, {
      type: 'bot',
      content: (
        <div className="space-y-4 animate-fade-in">
          <h3 className="font-bold">{translations[language].support}</h3>
          <p className="flex items-center gap-2">
            <FiClock />
            {translations[language].supportHours}
          </p>
          <div className="grid grid-cols-2 gap-4">
            <button className="flex items-center justify-center gap-2 bg-light-primary dark:bg-dark-primary text-white px-4 py-2 rounded-lg hover:opacity-90 transition-opacity">
              <FiMessageSquare />
              Live Chat
            </button>
            <button className="flex items-center justify-center gap-2 bg-green-600 text-white px-4 py-2 rounded-lg hover:opacity-90 transition-opacity">
              <FaWhatsapp />
              WhatsApp
            </button>
          </div>
        </div>
      )
    }]);
  };

  const handleEnquirySubmit = async (e) => {
    e.preventDefault();
    
    try {
      // Here you would typically send the form data to an API
      // const response = await axios.post('/api/enquiry', formData);
      
      setShowEnquiryForm(false);
      setFormData({ name: '', email: '', phone: '', message: '' });
      
      setMessages(prev => [...prev, {
        type: 'bot',
        content: translations[language].thankYou
      }]);
    } catch (error) {
      console.error('Error submitting enquiry:', error);
    }
  };

  // Enhanced message sending with Enter key support
  const handleKeyPress = (e) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      handleSubmit(e);
    }
  };

  return (
    <>
      {/* Chat Toggle Button */}
      <button
        onClick={() => setIsOpen(!isOpen)}
        className="fixed bottom-6 right-6 w-14 h-14 bg-[#000000] border border-[#00ff00] rounded-full flex items-center justify-center hover:shadow-[0_0_20px_#00ff00] transition-all duration-300 z-50"
      >
        <FiMessageCircle className="w-6 h-6 text-[#00ff00]" />
      </button>

      {/* Chat Window */}
      <div
        className={`
          fixed right-6 bottom-24
          w-[400px] h-[600px]
          bg-[#000000]
          rounded-lg
          flex flex-col
          transition-all duration-300
          ${isOpen ? 'opacity-100 scale-100' : 'opacity-0 scale-95 pointer-events-none'}
          border border-[#00ff00]/30
          overflow-hidden
          z-40
        `}
      >
        {/* Header */}
        <div className="flex items-center justify-between px-4 py-3 bg-[#000000] border-b border-[#00ff00]/30">
          <div className="flex items-center gap-2">
            <div className="w-5 h-5 rounded-full bg-[#00ff00]/20 flex items-center justify-center">
              <span className="text-[#00ff00] text-sm">âœ“</span>
            </div>
            <span className="text-[#00ff00] font-semibold">SwanSorter Assistant</span>
          </div>
          <div className="flex items-center gap-2">
            {/* Language Selector */}
            <div className="relative" ref={languageMenuRef}>
              <button
                onClick={() => setShowLanguageMenu(!showLanguageMenu)}
                className="flex items-center gap-1 px-2 py-1 text-[#00ff00] text-sm hover:bg-[#00ff00]/10 rounded transition-colors duration-200"
              >
                <FiGlobe className="w-4 h-4" />
                <span className="uppercase">{LANGUAGES[language].flag}</span>
                <FiChevronDown className={`w-4 h-4 transition-transform duration-200 ${showLanguageMenu ? 'transform rotate-180' : ''}`} />
              </button>
              
              {showLanguageMenu && (
                <div className="absolute right-0 mt-1 w-32 bg-[#000000] border border-[#00ff00]/30 rounded-lg overflow-hidden shadow-lg">
                  {Object.entries(LANGUAGES).map(([code, { label, flag }]) => (
                    <button
                      key={code}
                      onClick={() => {
                        setLanguage(code);
                        setShowLanguageMenu(false);
                      }}
                      className={`
                        w-full px-3 py-2 text-left text-sm flex items-center gap-2
                        ${language === code ? 'bg-[#00ff00]/10' : 'hover:bg-[#00ff00]/5'}
                        text-[#00ff00] transition-colors duration-200
                      `}
                    >
                      <span>{flag}</span>
                      <span>{label}</span>
                    </button>
                  ))}
                </div>
              )}
            </div>
            
            <button
              onClick={() => setIsOpen(false)}
              className="text-[#00ff00] hover:bg-[#00ff00]/10 p-1 rounded transition-colors duration-200"
            >
              <FiX className="w-4 h-4" />
            </button>
          </div>
        </div>

        {/* Messages */}
        <div className="flex-1 overflow-y-auto p-4 space-y-4">
          {messages.map((message, index) => (
            <div
              key={index}
              className={`flex ${message.type === MessageType.USER ? 'justify-end' : 'justify-start'}`}
            >
              <div
                className={`
                  max-w-[85%] p-3 rounded-lg
                  ${message.type === MessageType.USER
                    ? 'bg-[#00ff00] text-black ml-auto'
                    : message.type === MessageType.LOADING
                    ? 'bg-[#000000] text-[#00ff00]/70 border border-[#00ff00]/30 animate-pulse'
                    : 'bg-[#000000] text-[#00ff00] border border-[#00ff00]/30'
                  }
                  transition-all duration-300
                `}
              >
                {message.type === MessageType.LOADING ? (
                  <div className="flex items-center gap-2">
                    <div className="w-2 h-2 bg-[#00ff00] rounded-full animate-bounce" style={{ animationDelay: '0ms' }}></div>
                    <div className="w-2 h-2 bg-[#00ff00] rounded-full animate-bounce" style={{ animationDelay: '150ms' }}></div>
                    <div className="w-2 h-2 bg-[#00ff00] rounded-full animate-bounce" style={{ animationDelay: '300ms' }}></div>
                  </div>
                ) : typeof message.content === 'string' ? (
                  <p className="whitespace-pre-line">{message.content}</p>
                ) : (
                  message.content
                )}
              </div>
            </div>
          ))}
          <div ref={messagesEndRef} />
        </div>

        {/* Input */}
        <form 
          onSubmit={handleSubmit}
          className="p-4 border-t border-[#00ff00]/30 bg-[#000000]"
        >
          <div className="flex gap-2">
            <input
              type="text"
              value={inputValue}
              onChange={handleInputChange}
              onKeyPress={handleKeyPress}
              placeholder="Type a message..."
              className="flex-1 px-3 py-2 bg-[#000000] border border-[#00ff00]/30 rounded-md text-[#00ff00] placeholder-[#00ff00]/50 focus:outline-none focus:border-[#00ff00] focus:shadow-[0_0_5px_#00ff00] transition-all duration-300"
            />
            <Button
              type="submit"
              variant="outline"
              size="icon"
              disabled={!inputValue.trim() || isLoadingProducts}
              className="border-[#00ff00] text-[#00ff00] hover:bg-[#00ff00]/10 hover:border-[#00ff00] hover:shadow-[0_0_5px_#00ff00] disabled:opacity-50 disabled:cursor-not-allowed transition-all duration-300"
            >
              <FiSend className="w-4 h-4" />
            </Button>
          </div>
        </form>
      </div>
    </>
  );
};

export default ChatBot;
