import React, { useState, useRef, useEffect } from 'react';
import { FiSend, FiDownload, FiPhone, FiMail, FiMapPin, FiMessageSquare, FiGlobe, FiClock, FiMessageCircle } from 'react-icons/fi';
import { FaWhatsapp } from 'react-icons/fa';
import { Button } from './ui/Button';
import { Dialog } from './ui/Dialog';
import axios from 'axios';

const API_ENDPOINT = "https://crud-backend-a70z.onrender.com/api/products";

// Message types
const MessageType = {
  USER: "user",
  BOT: "bot",
  PRODUCT: "product",
  CONTACT: "contact",
  ENQUIRY: "enquiry",
  CATALOG: "catalog",
  SUPPORT: "support",
  ERROR: "error"
};

// Languages
const translations = {
  en: {
    welcome: "Hello! How can I assist you today? You can type:\n- 'Products' to see our data sorting solutions\n- 'Contact' for our details\n- 'Enquiry' to submit a request\n- 'Catalog' to download our catalog\n- 'Support' to speak with an agent",
    product: "Our Products",
    contact: "Contact Information",
    enquiry: "Submit Enquiry",
    support: "Support Options",
    catalog: "Download Catalog",
    thankYou: "Thank you for your enquiry! Our team will reach out to you shortly.",
    supportHours: "Support Hours: Monday - Friday, 9 AM - 6 PM EST",
    loading: "Loading products...",
    error: "Sorry, there was an error loading the products. Please try again.",
    price: "Price",
    viewMore: "View More",
    noProducts: "No products found.",
    currency: "USD"
  },
  es: {
    welcome: "¡Hola! ¿Cómo puedo ayudarte hoy? Puedes escribir:\n- 'Productos' para ver nuestras soluciones\n- 'Contacto' para nuestros detalles\n- 'Consulta' para enviar una solicitud\n- 'Catálogo' para descargar nuestro catálogo\n- 'Soporte' para hablar con un agente",
    product: "Nuestros Productos",
    contact: "Información de Contacto",
    enquiry: "Enviar Consulta",
    support: "Opciones de Soporte",
    catalog: "Descargar Catálogo",
    thankYou: "¡Gracias por tu consulta! Nuestro equipo se pondrá en contacto contigo pronto.",
    supportHours: "Horario de Soporte: Lunes - Viernes, 9 AM - 6 PM EST",
    loading: "Cargando productos...",
    error: "Lo siento, hubo un error al cargar los productos. Por favor, inténtalo de nuevo.",
    price: "Precio",
    viewMore: "Ver Más",
    noProducts: "No se encontraron productos.",
    currency: "USD"
  },
  hi: {
    welcome: "नमस्ते! मैं आपकी कैसे मदद कर सकता हूं? आप टाइप कर सकते हैं:\n- 'Products' उत्पाद देखने के लिए\n- 'Contact' संपर्क जानकारी के लिए\n- 'Enquiry' पूछताछ के लिए\n- 'Catalog' कैटलॉग डाउनलोड के लिए\n- 'Support' सहायता के लिए",
    product: "हमारे उत्पाद",
    contact: "संपर्क जानकारी",
    enquiry: "पूछताछ करें",
    support: "सहायता विकल्प",
    catalog: "कैटलॉग डाउनलोड करें",
    thankYou: "आपकी पूछताछ के लिए धन्यवाद! हमारी टीम जल्द ही आपसे संपर्क करेगी।",
    supportHours: "सहायता समय: सोमवार - शुक्रवार, सुबह 9 बजे - शाम 6 बजे EST",
    loading: "उत्पाद लोड हो रहे हैं...",
    error: "क्षमा करें, उत्पाद लोड करने में एक त्रुटि हुई। कृपया पुनः प्रयास करें।",
    price: "मूल्य",
    viewMore: "और देखें",
    noProducts: "कोई उत्पाद नहीं मिला।",
    currency: "USD"
  }
};

const ChatBot = () => {
  const [isOpen, setIsOpen] = useState(false);
  const [isLargeMode, setIsLargeMode] = useState(false);
  const [messages, setMessages] = useState([]);
  const [inputValue, setInputValue] = useState('');
  const [showEnquiryForm, setShowEnquiryForm] = useState(false);
  const [language, setLanguage] = useState('en');
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    phone: '',
    message: ''
  });
  const messagesEndRef = useRef(null);
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState(null);
  const [products, setProducts] = useState([]);

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  };

  useEffect(() => {
    // Detect user's language
    const userLang = navigator.language.split('-')[0];
    if (translations[userLang]) {
      setLanguage(userLang);
    }

    // Initial welcome message
    setMessages([{
      type: 'bot',
      content: translations[language].welcome
    }]);
  }, []);

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  const handleInputChange = (e) => {
    setInputValue(e.target.value);
  };

  const handleFormChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  // Function to fetch products from API
  const fetchProducts = async () => {
    setIsLoading(true);
    setError(null);
    try {
      const response = await axios.get(API_ENDPOINT);
      setProducts(response.data);
    } catch (err) {
      setError(err.message);
      console.error('Error fetching products:', err);
    } finally {
      setIsLoading(false);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const userMessage = inputValue.trim().toLowerCase();
    
    if (!userMessage) return;

    setMessages(prev => [...prev, { type: MessageType.USER, content: inputValue }]);
    setInputValue('');

    switch (userMessage) {
      case 'product':
      case 'products':
      case 'productos':
      case 'उत्पाद':
        await handleProductRequest();
        break;
      case 'contact':
      case 'contacto':
      case 'संपर्क':
        handleContactRequest();
        break;
      case 'enquiry':
      case 'consulta':
      case 'पूछताछ':
        setShowEnquiryForm(true);
        break;
      case 'catalog':
      case 'catálogo':
      case 'कैटलॉग':
        handleCatalogRequest();
        break;
      case 'support':
      case 'soporte':
      case 'सहायता':
        handleSupportRequest();
        break;
      default:
        setMessages(prev => [...prev, {
          type: MessageType.BOT,
          content: translations[language].welcome
        }]);
    }
  };

  // Modified product request handler
  const handleProductRequest = async () => {
    setMessages(prev => [...prev, {
      type: MessageType.BOT,
      content: translations[language].loading
    }]);

    await fetchProducts();

    if (error) {
      setMessages(prev => [...prev.slice(0, -1), {
        type: MessageType.ERROR,
        content: translations[language].error
      }]);
      return;
    }

    if (!products.length) {
      setMessages(prev => [...prev.slice(0, -1), {
        type: MessageType.BOT,
        content: translations[language].noProducts
      }]);
      return;
    }

    setMessages(prev => [...prev.slice(0, -1), {
      type: MessageType.PRODUCT,
      content: (
        <div className="space-y-4 animate-fade-in">
          <h3 className="font-bold text-lg text-light-primary dark:text-dark-primary">
            {translations[language].product}
          </h3>
          <div className="grid gap-4">
            {products.map((product, index) => (
              <div 
                key={product._id || index}
                className="p-4 bg-light-secondary dark:bg-dark-secondary rounded-lg animate-slide-up border border-light-primary/20 dark:border-dark-primary/20 hover:shadow-neon-sm transition-all duration-300"
                style={{ animationDelay: `${index * 100}ms` }}
              >
                <div className="flex items-start justify-between">
                  <div>
                    <h4 className="font-semibold text-light-primary dark:text-dark-primary">
                      {product.name}
                    </h4>
                    <p className="text-sm mt-2 text-light-foreground/80 dark:text-dark-foreground/80">
                      {product.description}
                    </p>
                  </div>
                  {product.image && (
                    <img 
                      src={product.image} 
                      alt={product.name}
                      className="w-20 h-20 object-cover rounded-lg ml-4"
                    />
                  )}
                </div>
                <div className="mt-3 flex items-center justify-between">
                  <p className="text-sm font-bold text-light-accent dark:text-dark-accent">
                    {product.price} {translations[language].currency}
                  </p>
                  <Button
                    variant="outline"
                    size="sm"
                    onClick={() => window.open(product.url || '#', '_blank')}
                    className="text-xs"
                  >
                    {translations[language].viewMore}
                  </Button>
                </div>
              </div>
            ))}
          </div>
        </div>
      )
    }]);
  };

  const handleContactRequest = () => {
    setMessages(prev => [...prev, {
      type: 'bot',
      content: (
        <div className="space-y-4 animate-fade-in">
          <h3 className="font-bold">{translations[language].contact}:</h3>
          <div className="space-y-2">
            <p className="flex items-center gap-2">
              <FiPhone className="text-light-primary dark:text-dark-primary" />
              <span>+1 234 567 890</span>
            </p>
            <p className="flex items-center gap-2">
              <FiMail className="text-light-primary dark:text-dark-primary" />
              <span>support@example.com</span>
            </p>
            <p className="flex items-center gap-2">
              <FiMapPin className="text-light-primary dark:text-dark-primary" />
              <span>123 Business Street, City, Country</span>
            </p>
            <button className="mt-4 bg-light-primary dark:bg-dark-primary text-white px-4 py-2 rounded-lg flex items-center gap-2 hover:opacity-90 transition-opacity">
              <FiPhone /> Call Us
            </button>
          </div>
        </div>
      )
    }]);
  };

  const handleCatalogRequest = () => {
    setMessages(prev => [...prev, {
      type: 'bot',
      content: (
        <div className="space-y-4 animate-fade-in">
          <h3 className="font-bold">{translations[language].catalog}</h3>
          <button 
            className="flex items-center gap-2 bg-light-primary dark:bg-dark-primary text-white px-4 py-2 rounded-lg hover:opacity-90 transition-opacity"
            onClick={() => window.open('/catalog.pdf', '_blank')}
          >
            <FiDownload />
            Download Now
          </button>
        </div>
      )
    }]);
  };

  const handleSupportRequest = () => {
    setMessages(prev => [...prev, {
      type: 'bot',
      content: (
        <div className="space-y-4 animate-fade-in">
          <h3 className="font-bold">{translations[language].support}</h3>
          <p className="flex items-center gap-2">
            <FiClock />
            {translations[language].supportHours}
          </p>
          <div className="grid grid-cols-2 gap-4">
            <button className="flex items-center justify-center gap-2 bg-light-primary dark:bg-dark-primary text-white px-4 py-2 rounded-lg hover:opacity-90 transition-opacity">
              <FiMessageSquare />
              Live Chat
            </button>
            <button className="flex items-center justify-center gap-2 bg-green-600 text-white px-4 py-2 rounded-lg hover:opacity-90 transition-opacity">
              <FaWhatsapp />
              WhatsApp
            </button>
          </div>
        </div>
      )
    }]);
  };

  const handleEnquirySubmit = async (e) => {
    e.preventDefault();
    
    try {
      // Here you would typically send the form data to an API
      // const response = await axios.post('/api/enquiry', formData);
      
      setShowEnquiryForm(false);
      setFormData({ name: '', email: '', phone: '', message: '' });
      
      setMessages(prev => [...prev, {
        type: 'bot',
        content: translations[language].thankYou
      }]);
    } catch (error) {
      console.error('Error submitting enquiry:', error);
    }
  };

  return (
    <>
      {/* Chat Toggle Button */}
      <button
        onClick={() => setIsOpen(!isOpen)}
        className={`
          fixed bottom-6 right-6 w-16 h-16
          bg-light-background dark:bg-dark-background
          text-light-primary dark:text-dark-primary
          rounded-full
          flex items-center justify-center
          hover:scale-110 active:scale-95
          transition-all duration-200
          z-50
          shadow-neon hover:shadow-neon-lg
          animate-pulse-neon
          border border-light-primary dark:border-dark-primary
        `}
        aria-label="Toggle chat"
      >
        <FiMessageCircle size={24} className="animate-glow" />
      </button>

      {/* Chat Window */}
      <div
        className={`
          fixed right-6 bottom-24
          w-[90vw] max-w-[400px]
          ${isLargeMode ? 'lg:w-[95vw] lg:max-w-[800px]' : ''}
          h-[70vh] max-h-[500px]
          ${isLargeMode ? 'lg:h-[85vh] lg:max-h-[800px]' : ''}
          bg-light-background dark:bg-dark-background
          rounded-lg
          flex flex-col
          transform transition-all duration-300
          ${isOpen ? 'scale-100 opacity-100' : 'scale-95 opacity-0 pointer-events-none'}
          border border-light-primary dark:border-dark-primary
          overflow-hidden
          backdrop-blur-sm
          z-40
          shadow-neon
        `}
      >
        {/* Header */}
        <div className="flex items-center justify-between p-4 border-b border-light-primary dark:border-dark-primary bg-light-secondary dark:bg-dark-secondary">
          <div className="flex items-center gap-2">
            <img src="/swansorter.png" alt="SwanSorter" className="h-8 w-8 animate-float" />
            <h2 className="font-bold text-lg font-mono text-light-primary dark:text-dark-primary animate-glow">
              SwanSorter Support
            </h2>
          </div>
          <div className="flex items-center gap-2">
            <button
              onClick={() => setLanguage(language === 'en' ? 'es' : 'en')}
              className="p-2 rounded-lg hover:bg-light-input dark:hover:bg-dark-input transition-colors text-light-primary dark:text-dark-primary hover:shadow-neon-sm"
              aria-label="Change language"
            >
              <FiGlobe className="animate-glow" />
            </button>
            <button
              onClick={() => setIsLargeMode(!isLargeMode)}
              className="p-2 rounded-lg hover:bg-light-input dark:hover:bg-dark-input transition-colors text-light-primary dark:text-dark-primary hover:shadow-neon-sm"
              aria-label={isLargeMode ? "Switch to normal mode" : "Switch to large mode"}
            >
              <span className="text-sm font-mono">{isLargeMode ? "Normal" : "Large"}</span>
            </button>
          </div>
        </div>

        {/* Messages Container */}
        <div className="flex-1 overflow-y-auto p-4 space-y-4 bg-light-background dark:bg-dark-background">
          {messages.map((message, index) => (
            <div
              key={index}
              className={`flex ${message.type === 'user' ? 'justify-end' : 'justify-start'}`}
            >
              <div
                className={`
                  max-w-[80%] p-3 rounded-lg animate-slide-up
                  ${message.type === 'user'
                    ? 'bg-light-primary dark:bg-dark-primary text-light-background dark:text-dark-background shadow-neon'
                    : 'bg-light-secondary dark:bg-dark-secondary text-light-primary dark:text-dark-primary'
                  }
                  hover:shadow-neon-sm transition-shadow
                `}
                style={{ animationDelay: `${index * 100}ms` }}
              >
                {typeof message.content === 'string' ? (
                  <p className="text-balance font-mono">{message.content}</p>
                ) : (
                  message.content
                )}
              </div>
            </div>
          ))}
          <div ref={messagesEndRef} />
        </div>

        {/* Input Section */}
        {showEnquiryForm ? (
          <form onSubmit={handleEnquirySubmit} className="p-4 bg-light-card dark:bg-dark-card animate-slide-up border-t border-light-primary dark:border-dark-primary">
            <div className="space-y-4">
              <input
                type="text"
                name="name"
                placeholder="Your Name"
                required
                className="w-full p-2 rounded-lg border border-light-primary dark:border-dark-primary bg-light-input dark:bg-dark-input text-light-primary dark:text-dark-primary placeholder-light-muted dark:placeholder-dark-muted focus:ring-2 focus:ring-light-accent dark:focus:ring-dark-accent focus:shadow-neon-sm"
                value={formData.name}
                onChange={handleFormChange}
              />
              <input
                type="email"
                name="email"
                placeholder="Your Email"
                required
                pattern="[a-z0-9._%+-]+@[a-z0-9.-]+\.[a-z]{2,}$"
                className="w-full p-2 rounded-lg border border-light-primary dark:border-dark-primary bg-light-input dark:bg-dark-input text-light-primary dark:text-dark-primary placeholder-light-muted dark:placeholder-dark-muted focus:ring-2 focus:ring-light-accent dark:focus:ring-dark-accent focus:shadow-neon-sm"
                value={formData.email}
                onChange={handleFormChange}
              />
              <input
                type="tel"
                name="phone"
                placeholder="Your Phone Number"
                required
                pattern="[0-9]{10}"
                className="w-full p-2 rounded-lg border border-light-primary dark:border-dark-primary bg-light-input dark:bg-dark-input text-light-primary dark:text-dark-primary placeholder-light-muted dark:placeholder-dark-muted focus:ring-2 focus:ring-light-accent dark:focus:ring-dark-accent focus:shadow-neon-sm"
                value={formData.phone}
                onChange={handleFormChange}
              />
              <textarea
                name="message"
                placeholder="Your Message (Optional)"
                className="w-full p-2 rounded-lg border border-light-primary dark:border-dark-primary bg-light-input dark:bg-dark-input text-light-primary dark:text-dark-primary placeholder-light-muted dark:placeholder-dark-muted focus:ring-2 focus:ring-light-accent dark:focus:ring-dark-accent focus:shadow-neon-sm"
                value={formData.message}
                onChange={handleFormChange}
                rows="4"
              />
              <div className="flex gap-2">
                <Button
                  type="submit"
                  variant="default"
                  className="flex-1 shadow-neon hover:shadow-neon-lg"
                >
                  Submit
                </Button>
                <Button
                  type="button"
                  variant="destructive"
                  className="flex-1"
                  onClick={() => setShowEnquiryForm(false)}
                >
                  Cancel
                </Button>
              </div>
            </div>
          </form>
        ) : (
          <form onSubmit={handleSubmit} className="p-4 border-t border-light-primary dark:border-dark-primary bg-light-card dark:bg-dark-card">
            <div className="flex gap-2">
              <input
                type="text"
                value={inputValue}
                onChange={handleInputChange}
                placeholder="Type your message..."
                className="flex-1 p-2 rounded-lg border border-light-primary dark:border-dark-primary bg-light-input dark:bg-dark-input text-light-primary dark:text-dark-primary placeholder-light-muted dark:placeholder-dark-muted focus:ring-2 focus:ring-light-accent dark:focus:ring-dark-accent focus:shadow-neon-sm"
              />
              <Button
                type="submit"
                variant="default"
                size="icon"
                className="shadow-neon hover:shadow-neon-lg"
                aria-label="Send message"
              >
                <FiSend className="animate-glow" />
              </Button>
            </div>
          </form>
        )}
      </div>
    </>
  );
};

export default ChatBot;
