import React, { useState, useRef, useEffect } from 'react';
import { FiSend, FiDownload, FiPhone, FiMail, FiMapPin, FiMessageSquare, FiGlobe, FiClock, FiWhatsapp } from 'react-icons/fi';

const ChatBot = () => {
  const [messages, setMessages] = useState([]);
  const [inputValue, setInputValue] = useState('');
  const [showEnquiryForm, setShowEnquiryForm] = useState(false);
  const [showSupportOptions, setShowSupportOptions] = useState(false);
  const [language, setLanguage] = useState('en');
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    phone: '',
    message: ''
  });
  const messagesEndRef = useRef(null);

  const translations = {
    en: {
      welcome: "Hello! How can I assist you today? You can type 'Product' to see our product list, 'Contact' to get our details, 'Enquiry' to submit a request, 'Catalog' to download our catalog, or 'Support' to speak with an agent.",
      product: "Our Products",
      contact: "Contact Information",
      enquiry: "Submit Enquiry",
      support: "Support Options",
      catalog: "Download Catalog",
      thankYou: "Thank you for your enquiry! Our team will reach out to you shortly.",
      supportHours: "Support Hours: Monday - Friday, 9 AM - 6 PM EST"
    },
    es: {
      welcome: "¡Hola! ¿Cómo puedo ayudarte hoy? Escribe 'Producto' para ver nuestra lista de productos, 'Contacto' para obtener nuestros detalles, 'Consulta' para enviar una solicitud, 'Catálogo' para descargar nuestro catálogo o 'Soporte' para hablar con un agente.",
      // Add other Spanish translations
    }
  };

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  };

  useEffect(() => {
    // Detect user's language
    const userLang = navigator.language.split('-')[0];
    if (translations[userLang]) {
      setLanguage(userLang);
    }

    // Initial welcome message
    setMessages([{
      type: 'bot',
      content: translations[language].welcome
    }]);
  }, []);

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  const handleInputChange = (e) => {
    setInputValue(e.target.value);
  };

  const handleFormChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const userMessage = inputValue.trim().toLowerCase();
    
    if (!userMessage) return;

    setMessages(prev => [...prev, { type: 'user', content: inputValue }]);
    setInputValue('');

    switch (userMessage) {
      case 'product':
        handleProductRequest();
        break;
      case 'contact':
        handleContactRequest();
        break;
      case 'enquiry':
        setShowEnquiryForm(true);
        break;
      case 'catalog':
        handleCatalogRequest();
        break;
      case 'support':
        handleSupportRequest();
        break;
      default:
        setMessages(prev => [...prev, {
          type: 'bot',
          content: translations[language].welcome
        }]);
    }
  };

  const handleProductRequest = () => {
    const products = [
      { 
        name: 'Premium Widget',
        description: 'High-quality widget for professional use',
        price: '$199.99',
        image: 'widget.jpg'
      },
      {
        name: 'Super Gadget',
        description: 'Next-generation gadget with smart features',
        price: '$299.99',
        image: 'gadget.jpg'
      },
      {
        name: 'Ultra Device',
        description: 'Revolutionary device for everyday use',
        price: '$399.99',
        image: 'device.jpg'
      }
    ];

    setMessages(prev => [...prev, {
      type: 'bot',
      content: (
        <div className="space-y-4 animate-fade-in">
          <h3 className="font-bold">{translations[language].product}:</h3>
          <div className="grid gap-4">
            {products.map((product, index) => (
              <div key={index} className="p-4 bg-light-secondary dark:bg-dark-secondary rounded-lg animate-slide-up" style={{ animationDelay: `${index * 100}ms` }}>
                <h4 className="font-semibold">{product.name}</h4>
                <p className="text-sm mt-2">{product.description}</p>
                <p className="text-sm font-bold mt-1">{product.price}</p>
                <button className="mt-2 text-sm text-blue-600 dark:text-blue-400 hover:underline">
                  View More
                </button>
              </div>
            ))}
          </div>
        </div>
      )
    }]);
  };

  const handleContactRequest = () => {
    setMessages(prev => [...prev, {
      type: 'bot',
      content: (
        <div className="space-y-4 animate-fade-in">
          <h3 className="font-bold">{translations[language].contact}:</h3>
          <div className="space-y-2">
            <p className="flex items-center gap-2">
              <FiPhone className="text-light-primary dark:text-dark-primary" />
              <span>+1 234 567 890</span>
            </p>
            <p className="flex items-center gap-2">
              <FiMail className="text-light-primary dark:text-dark-primary" />
              <span>support@example.com</span>
            </p>
            <p className="flex items-center gap-2">
              <FiMapPin className="text-light-primary dark:text-dark-primary" />
              <span>123 Business Street, City, Country</span>
            </p>
            <button className="mt-4 bg-light-primary dark:bg-dark-primary text-white px-4 py-2 rounded-lg flex items-center gap-2 hover:opacity-90 transition-opacity">
              <FiPhone /> Call Us
            </button>
          </div>
        </div>
      )
    }]);
  };

  const handleCatalogRequest = () => {
    setMessages(prev => [...prev, {
      type: 'bot',
      content: (
        <div className="space-y-4 animate-fade-in">
          <h3 className="font-bold">{translations[language].catalog}</h3>
          <button 
            className="flex items-center gap-2 bg-light-primary dark:bg-dark-primary text-white px-4 py-2 rounded-lg hover:opacity-90 transition-opacity"
            onClick={() => window.open('/catalog.pdf', '_blank')}
          >
            <FiDownload />
            Download Now
          </button>
        </div>
      )
    }]);
  };

  const handleSupportRequest = () => {
    setMessages(prev => [...prev, {
      type: 'bot',
      content: (
        <div className="space-y-4 animate-fade-in">
          <h3 className="font-bold">{translations[language].support}</h3>
          <p className="flex items-center gap-2">
            <FiClock />
            {translations[language].supportHours}
          </p>
          <div className="grid grid-cols-2 gap-4">
            <button className="flex items-center justify-center gap-2 bg-light-primary dark:bg-dark-primary text-white px-4 py-2 rounded-lg hover:opacity-90 transition-opacity">
              <FiMessageSquare />
              Live Chat
            </button>
            <button className="flex items-center justify-center gap-2 bg-green-600 text-white px-4 py-2 rounded-lg hover:opacity-90 transition-opacity">
              <FiWhatsapp />
              WhatsApp
            </button>
          </div>
        </div>
      )
    }]);
  };

  const handleEnquirySubmit = async (e) => {
    e.preventDefault();
    
    try {
      // Here you would typically send the form data to an API
      // const response = await axios.post('/api/enquiry', formData);
      
      setShowEnquiryForm(false);
      setFormData({ name: '', email: '', phone: '', message: '' });
      
      setMessages(prev => [...prev, {
        type: 'bot',
        content: translations[language].thankYou
      }]);
    } catch (error) {
      console.error('Error submitting enquiry:', error);
    }
  };

  return (
    <div className="bg-white dark:bg-dark-background rounded-lg shadow-lg overflow-hidden">
      <div className="flex justify-end p-2 border-b border-gray-200 dark:border-gray-800">
        <button
          onClick={() => setLanguage(language === 'en' ? 'es' : 'en')}
          className="flex items-center gap-1 text-sm text-gray-600 dark:text-gray-400 hover:text-gray-900 dark:hover:text-gray-100"
        >
          <FiGlobe />
          {language.toUpperCase()}
        </button>
      </div>
      
      <div className="h-[500px] overflow-y-auto p-4 space-y-4">
        {messages.map((message, index) => (
          <div
            key={index}
            className={`flex ${message.type === 'user' ? 'justify-end' : 'justify-start'}`}
          >
            <div
              className={`max-w-[80%] p-3 rounded-lg animate-slide-up ${
                message.type === 'user'
                  ? 'bg-light-primary dark:bg-dark-primary text-white'
                  : 'bg-light-secondary dark:bg-dark-secondary'
              }`}
              style={{ animationDelay: `${index * 100}ms` }}
            >
              {typeof message.content === 'string' ? (
                <p className="text-balance">{message.content}</p>
              ) : (
                message.content
              )}
            </div>
          </div>
        ))}
        <div ref={messagesEndRef} />
      </div>

      {showEnquiryForm ? (
        <form onSubmit={handleEnquirySubmit} className="p-4 bg-light-secondary dark:bg-dark-secondary animate-slide-up">
          <div className="space-y-4">
            <input
              type="text"
              name="name"
              placeholder="Your Name"
              required
              className="w-full p-2 rounded-lg border border-gray-300 dark:border-gray-700 bg-white dark:bg-dark-background focus:ring-2 focus:ring-light-primary dark:focus:ring-dark-primary"
              value={formData.name}
              onChange={handleFormChange}
            />
            <input
              type="email"
              name="email"
              placeholder="Your Email"
              required
              pattern="[a-z0-9._%+-]+@[a-z0-9.-]+\.[a-z]{2,}$"
              className="w-full p-2 rounded-lg border border-gray-300 dark:border-gray-700 bg-white dark:bg-dark-background focus:ring-2 focus:ring-light-primary dark:focus:ring-dark-primary"
              value={formData.email}
              onChange={handleFormChange}
            />
            <input
              type="tel"
              name="phone"
              placeholder="Your Phone Number"
              required
              pattern="[0-9]{10}"
              className="w-full p-2 rounded-lg border border-gray-300 dark:border-gray-700 bg-white dark:bg-dark-background focus:ring-2 focus:ring-light-primary dark:focus:ring-dark-primary"
              value={formData.phone}
              onChange={handleFormChange}
            />
            <textarea
              name="message"
              placeholder="Your Message (Optional)"
              className="w-full p-2 rounded-lg border border-gray-300 dark:border-gray-700 bg-white dark:bg-dark-background focus:ring-2 focus:ring-light-primary dark:focus:ring-dark-primary"
              value={formData.message}
              onChange={handleFormChange}
              rows="4"
            />
            <div className="flex gap-2">
              <button
                type="submit"
                className="flex-1 bg-light-primary dark:bg-dark-primary text-white px-4 py-2 rounded-lg hover:opacity-90 transition-opacity"
              >
                Submit
              </button>
              <button
                type="button"
                onClick={() => setShowEnquiryForm(false)}
                className="flex-1 bg-light-destructive dark:bg-dark-destructive text-white px-4 py-2 rounded-lg hover:opacity-90 transition-opacity"
              >
                Cancel
              </button>
            </div>
          </div>
        </form>
      ) : (
        <form onSubmit={handleSubmit} className="p-4 border-t border-gray-200 dark:border-gray-800">
          <div className="flex gap-2">
            <input
              type="text"
              value={inputValue}
              onChange={handleInputChange}
              placeholder="Type your message..."
              className="flex-1 p-2 rounded-lg border border-gray-300 dark:border-gray-700 bg-white dark:bg-dark-background focus:ring-2 focus:ring-light-primary dark:focus:ring-dark-primary"
            />
            <button
              type="submit"
              className="bg-light-primary dark:bg-dark-primary text-white p-2 rounded-lg hover:opacity-90 transition-opacity"
            >
              <FiSend />
            </button>
          </div>
        </form>
      )}
    </div>
  );
};

export default ChatBot;
