import React from 'react';
import { cva } from 'class-variance-authority';
import { cn } from '../../lib/utils';

const buttonVariants = cva(
  "inline-flex items-center justify-center rounded-md text-sm font-medium ring-offset-background transition-colors focus-visible:outline-none focus-visible:ring-2 focus-visible:ring-offset-2 disabled:pointer-events-none disabled:opacity-50",
  {
    variants: {
      variant: {
        default: "bg-light-primary dark:bg-dark-primary text-white hover:bg-light-primary/90 dark:hover:bg-dark-primary/90 shadow-sm hover:shadow-glow",
        destructive: "bg-light-destructive dark:bg-dark-destructive text-white hover:bg-light-destructive/90 dark:hover:bg-dark-destructive/90",
        outline: "border-2 border-light-border dark:border-dark-border bg-transparent hover:bg-light-secondary dark:hover:bg-dark-secondary",
        secondary: "bg-light-secondary dark:bg-dark-secondary text-light-foreground dark:text-dark-foreground hover:bg-light-secondary/80 dark:hover:bg-dark-secondary/80",
        ghost: "hover:bg-light-secondary dark:hover:bg-dark-secondary",
        link: "text-light-primary dark:text-dark-primary underline-offset-4 hover:underline"
      },
      size: {
        default: "h-10 px-4 py-2",
        sm: "h-8 px-3 text-xs",
        lg: "h-12 px-8 text-base",
        icon: "h-10 w-10"
      }
    },
    defaultVariants: {
      variant: "default",
      size: "default"
    }
  }
);

const Button = React.forwardRef(({ 
  className, 
  variant, 
  size, 
  children,
  ...props 
}, ref) => {
  return (
    <button
      className={cn(buttonVariants({ variant, size }), className)}
      ref={ref}
      {...props}
    >
      {children}
    </button>
  );
});

Button.displayName = "Button";

export { Button, buttonVariants };
