import React, { useState, useRef, useEffect } from 'react';
import { FiSend, FiDownload, FiPhone, FiMail, FiMapPin, FiMessageSquare, FiGlobe, FiClock, FiMessageCircle, FiX } from 'react-icons/fi';
import { FaWhatsapp } from 'react-icons/fa';
import { Button } from './ui/Button';
import { Dialog } from './ui/Dialog';
import axios from 'axios';

const API_ENDPOINT = "https://crud-backend-a70z.onrender.com/api/products";

// Message types
const MessageType = {
  USER: "user",
  BOT: "bot",
  PRODUCT: "product",
  CONTACT: "contact",
  ENQUIRY: "enquiry",
  CATALOG: "catalog",
  SUPPORT: "support",
  ERROR: "error"
};

// Languages
const translations = {
  en: {
    welcome: "Hello! How can I assist you today? You can type:\n- 'Products' to see our data sorting solutions\n- 'Contact' for our details\n- 'Enquiry' to submit a request\n- 'Catalog' to download our catalog\n- 'Support' to speak with an agent",
    product: "Our Products",
    contact: "Contact Information",
    enquiry: "Submit Enquiry",
    support: "Support Options",
    catalog: "Download Catalog",
    thankYou: "Thank you for your enquiry! Our team will reach out to you shortly.",
    supportHours: "Support Hours: Monday - Friday, 9 AM - 6 PM EST",
    loading: "Loading products...",
    error: "Sorry, there was an error loading the products. Please try again.",
    price: "Price",
    viewMore: "View More",
    noProducts: "No products found.",
    currency: "USD"
  },
  es: {
    welcome: "¡Hola! ¿Cómo puedo ayudarte hoy? Puedes escribir:\n- 'Productos' para ver nuestras soluciones\n- 'Contacto' para nuestros detalles\n- 'Consulta' para enviar una solicitud\n- 'Catálogo' para descargar nuestro catálogo\n- 'Soporte' para hablar con un agente",
    product: "Nuestros Productos",
    contact: "Información de Contacto",
    enquiry: "Enviar Consulta",
    support: "Opciones de Soporte",
    catalog: "Descargar Catálogo",
    thankYou: "¡Gracias por tu consulta! Nuestro equipo se pondrá en contacto contigo pronto.",
    supportHours: "Horario de Soporte: Lunes - Viernes, 9 AM - 6 PM EST",
    loading: "Cargando productos...",
    error: "Lo siento, hubo un error al cargar los productos. Por favor, inténtalo de nuevo.",
    price: "Precio",
    viewMore: "Ver Más",
    noProducts: "No se encontraron productos.",
    currency: "USD"
  },
  hi: {
    welcome: "नमस्ते! मैं आपकी कैसे मदद कर सकता हूं? आप टाइप कर सकते हैं:\n- 'Products' उत्पाद देखने के लिए\n- 'Contact' संपर्क जानकारी के लिए\n- 'Enquiry' पूछताछ के लिए\n- 'Catalog' कैटलॉग डाउनलोड के लिए\n- 'Support' सहायता के लिए",
    product: "हमारे उत्पाद",
    contact: "संपर्क जानकारी",
    enquiry: "पूछताछ करें",
    support: "सहायता विकल्प",
    catalog: "कैटलॉग डाउनलोड करें",
    thankYou: "आपकी पूछताछ के लिए धन्यवाद! हमारी टीम जल्द ही आपसे संपर्क करेगी।",
    supportHours: "सहायता समय: सोमवार - शुक्रवार, सुबह 9 बजे - शाम 6 बजे EST",
    loading: "उत्पाद लोड हो रहे हैं...",
    error: "क्षमा करें, उत्पाद लोड करने में एक त्रुटि हुई। कृपया पुनः प्रयास करें।",
    price: "मूल्य",
    viewMore: "और देखें",
    noProducts: "कोई उत्पाद नहीं मिला।",
    currency: "USD"
  }
};

const ChatBot = () => {
  const [isOpen, setIsOpen] = useState(false);
  const [isLargeMode, setIsLargeMode] = useState(false);
  const [messages, setMessages] = useState([]);
  const [inputValue, setInputValue] = useState('');
  const [showEnquiryForm, setShowEnquiryForm] = useState(false);
  const [language, setLanguage] = useState('en');
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    phone: '',
    message: ''
  });
  const messagesEndRef = useRef(null);
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState(null);
  const [products, setProducts] = useState([]);

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  };

  useEffect(() => {
    // Detect user's language
    const userLang = navigator.language.split('-')[0];
    if (translations[userLang]) {
      setLanguage(userLang);
    }

    // Initial welcome message
    setMessages([{
      type: 'bot',
      content: translations[language].welcome
    }]);
  }, []);

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  const handleInputChange = (e) => {
    setInputValue(e.target.value);
  };

  const handleFormChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  // Function to fetch products from API
  const fetchProducts = async () => {
    setIsLoading(true);
    setError(null);
    try {
      const response = await axios.get(API_ENDPOINT);
      setProducts(response.data);
    } catch (err) {
      setError(err.message);
      console.error('Error fetching products:', err);
    } finally {
      setIsLoading(false);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const userMessage = inputValue.trim().toLowerCase();
    
    if (!userMessage) return;

    setMessages(prev => [...prev, { type: MessageType.USER, content: inputValue }]);
    setInputValue('');

    switch (userMessage) {
      case 'product':
      case 'products':
      case 'productos':
      case 'उत्पाद':
        await handleProductRequest();
        break;
      case 'contact':
      case 'contacto':
      case 'संपर्क':
        handleContactRequest();
        break;
      case 'enquiry':
      case 'consulta':
      case 'पूछताछ':
        setShowEnquiryForm(true);
        break;
      case 'catalog':
      case 'catálogo':
      case 'कैटलॉग':
        handleCatalogRequest();
        break;
      case 'support':
      case 'soporte':
      case 'सहायता':
        handleSupportRequest();
        break;
      default:
        setMessages(prev => [...prev, {
          type: MessageType.BOT,
          content: translations[language].welcome
        }]);
    }
  };

  // Modified product request handler
  const handleProductRequest = async () => {
    setMessages(prev => [...prev, {
      type: MessageType.BOT,
      content: (
        <div className="space-y-4">
          <h3 className="text-[#00ff00] font-semibold mb-4">Here are our SwanSorter solutions:</h3>
          <div className="grid grid-cols-1 gap-4">
            {[
              {
                name: "Special Purpose Single Chute",
                description: "Special Purpose Single Chute Color Sorting Machine",
                price: "₹20,000",
                id: 1
              },
              {
                name: "Multi Purpose Four Chute",
                description: "Multi Purpose Four Chute . Multi Purpose Four Chute .",
                price: "₹22,222",
                id: 2
              },
              {
                name: "Multi Purpose Five Chute Machine",
                description: "Multi Purpose Five Chute Machine",
                price: "₹10,000",
                id: 3
              },
              {
                name: "CONVEYOR TYPE MACHINE",
                description: "CONVEYOR TYPE MACHINE",
                price: "₹9,00,000",
                id: 4
              },
              {
                name: "Multipurpose Three chute Mchine",
                description: "Multipurpose Three chute Mchine",
                price: "₹3,89,999",
                id: 5
              },
              {
                name: "Multipurpose Two Chute Machine",
                description: "Multipurpose Two Chute Machine",
                price: "₹11,111",
                id: 6
              }
            ].map((product) => (
              <div 
                key={product.id}
                className="bg-[#000000] border border-[#00ff00]/30 rounded-lg p-4 hover:border-[#00ff00]/50 transition-all"
              >
                <h4 className="text-[#00ff00] font-semibold mb-2">{product.name}</h4>
                <p className="text-[#00ff00]/80 text-sm mb-3">{product.description}</p>
                <div className="flex justify-between items-center">
                  <span className="text-[#00ff00] font-bold">{product.price}</span>
                  <Button
                    variant="outline"
                    size="sm"
                    className="text-[#00ff00] border-[#00ff00] hover:bg-[#00ff00]/10"
                  >
                    Learn More
                  </Button>
                </div>
              </div>
            ))}
          </div>
        </div>
      )
    }]);
  };

  const handleContactRequest = () => {
    setMessages(prev => [...prev, {
      type: 'bot',
      content: (
        <div className="space-y-4 animate-fade-in">
          <h3 className="font-bold">{translations[language].contact}:</h3>
          <div className="space-y-2">
            <p className="flex items-center gap-2">
              <FiPhone className="text-light-primary dark:text-dark-primary" />
              <span>+1 234 567 890</span>
            </p>
            <p className="flex items-center gap-2">
              <FiMail className="text-light-primary dark:text-dark-primary" />
              <span>support@example.com</span>
            </p>
            <p className="flex items-center gap-2">
              <FiMapPin className="text-light-primary dark:text-dark-primary" />
              <span>123 Business Street, City, Country</span>
            </p>
            <button className="mt-4 bg-light-primary dark:bg-dark-primary text-white px-4 py-2 rounded-lg flex items-center gap-2 hover:opacity-90 transition-opacity">
              <FiPhone /> Call Us
            </button>
          </div>
        </div>
      )
    }]);
  };

  const handleCatalogRequest = () => {
    setMessages(prev => [...prev, {
      type: 'bot',
      content: (
        <div className="space-y-4 animate-fade-in">
          <h3 className="font-bold">{translations[language].catalog}</h3>
          <button 
            className="flex items-center gap-2 bg-light-primary dark:bg-dark-primary text-white px-4 py-2 rounded-lg hover:opacity-90 transition-opacity"
            onClick={() => window.open('/catalog.pdf', '_blank')}
          >
            <FiDownload />
            Download Now
          </button>
        </div>
      )
    }]);
  };

  const handleSupportRequest = () => {
    setMessages(prev => [...prev, {
      type: 'bot',
      content: (
        <div className="space-y-4 animate-fade-in">
          <h3 className="font-bold">{translations[language].support}</h3>
          <p className="flex items-center gap-2">
            <FiClock />
            {translations[language].supportHours}
          </p>
          <div className="grid grid-cols-2 gap-4">
            <button className="flex items-center justify-center gap-2 bg-light-primary dark:bg-dark-primary text-white px-4 py-2 rounded-lg hover:opacity-90 transition-opacity">
              <FiMessageSquare />
              Live Chat
            </button>
            <button className="flex items-center justify-center gap-2 bg-green-600 text-white px-4 py-2 rounded-lg hover:opacity-90 transition-opacity">
              <FaWhatsapp />
              WhatsApp
            </button>
          </div>
        </div>
      )
    }]);
  };

  const handleEnquirySubmit = async (e) => {
    e.preventDefault();
    
    try {
      // Here you would typically send the form data to an API
      // const response = await axios.post('/api/enquiry', formData);
      
      setShowEnquiryForm(false);
      setFormData({ name: '', email: '', phone: '', message: '' });
      
      setMessages(prev => [...prev, {
        type: 'bot',
        content: translations[language].thankYou
      }]);
    } catch (error) {
      console.error('Error submitting enquiry:', error);
    }
  };

  return (
    <>
      {/* Chat Toggle Button */}
      <button
        onClick={() => setIsOpen(!isOpen)}
        className="fixed bottom-6 right-6 w-14 h-14 bg-[#000000] border border-[#00ff00] rounded-full flex items-center justify-center hover:shadow-[0_0_20px_#00ff00] transition-all z-50"
      >
        <FiMessageCircle className="w-6 h-6 text-[#00ff00]" />
      </button>

      {/* Chat Window */}
      <div
        className={`
          fixed right-6 bottom-24
          w-[400px] h-[600px]
          bg-[#000000]
          rounded-lg
          flex flex-col
          transition-all duration-300
          ${isOpen ? 'opacity-100 scale-100' : 'opacity-0 scale-95 pointer-events-none'}
          border border-[#00ff00]/30
          overflow-hidden
          z-40
        `}
      >
        {/* Header */}
        <div className="flex items-center justify-between px-4 py-3 bg-[#000000] border-b border-[#00ff00]/30">
          <div className="flex items-center gap-2">
            <div className="w-5 h-5 rounded-full bg-[#00ff00]/20 flex items-center justify-center">
              <span className="text-[#00ff00] text-sm">✓</span>
            </div>
            <span className="text-[#00ff00] font-semibold">SwanSorter Assistant</span>
          </div>
          <div className="flex items-center gap-2">
            <button
              onClick={() => setLanguage(prev => prev === 'en' ? 'es' : 'en')}
              className="flex items-center gap-1 px-2 py-1 text-[#00ff00] text-sm hover:bg-[#00ff00]/10 rounded"
            >
              <FiGlobe className="w-4 h-4" />
              <span className="uppercase">{language}</span>
            </button>
            <button
              onClick={() => setIsOpen(false)}
              className="text-[#00ff00] hover:bg-[#00ff00]/10 p-1 rounded"
            >
              <FiX className="w-4 h-4" />
            </button>
          </div>
        </div>

        {/* Messages */}
        <div className="flex-1 overflow-y-auto p-4 space-y-4">
          {messages.map((message, index) => (
            <div
              key={index}
              className={`flex ${message.type === MessageType.USER ? 'justify-end' : 'justify-start'}`}
            >
              <div
                className={`
                  max-w-[85%] p-3 rounded-lg
                  ${message.type === MessageType.USER
                    ? 'bg-[#00ff00] text-black ml-auto'
                    : 'bg-[#000000] text-[#00ff00] border border-[#00ff00]/30'
                  }
                `}
              >
                {typeof message.content === 'string' ? (
                  <p className="whitespace-pre-line">{message.content}</p>
                ) : (
                  message.content
                )}
              </div>
            </div>
          ))}
          <div ref={messagesEndRef} />
        </div>

        {/* Input */}
        <form 
          onSubmit={handleSubmit}
          className="p-4 border-t border-[#00ff00]/30 bg-[#000000]"
        >
          <div className="flex gap-2">
            <input
              type="text"
              value={inputValue}
              onChange={handleInputChange}
              onKeyDown={(e) => e.key === 'Enter' && handleSubmit(e)}
              placeholder="Type a message..."
              className="flex-1 px-3 py-2 bg-[#000000] border border-[#00ff00]/30 rounded-md text-[#00ff00] placeholder-[#00ff00]/50 focus:outline-none focus:border-[#00ff00]"
            />
            <Button
              type="submit"
              variant="outline"
              size="icon"
              className="border-[#00ff00] text-[#00ff00] hover:bg-[#00ff00]/10"
            >
              <FiSend className="w-4 h-4" />
            </Button>
          </div>
        </form>
      </div>
    </>
  );
};

export default ChatBot;
