import React, { useState, useRef, useEffect } from 'react';
import { FiSend, FiDownload, FiPhone, FiMail, FiMapPin, FiMessageSquare, FiGlobe, FiClock, FiMessageCircle, FiChevronDown, FiMaximize2, FiX } from 'react-icons/fi';
import { FaWhatsapp } from 'react-icons/fa';
import { Button } from './ui/Button';
import { Dialog } from './ui/Dialog';

const ChatBot = () => {
  const [isOpen, setIsOpen] = useState(false);
  const [isLargeMode, setIsLargeMode] = useState(false);
  const [messages, setMessages] = useState([]);
  const [inputValue, setInputValue] = useState('');
  const [showEnquiryForm, setShowEnquiryForm] = useState(false);
  const [showProducts, setShowProducts] = useState(false);
  const [language, setLanguage] = useState('en');
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    phone: '',
    message: ''
  });
  const messagesEndRef = useRef(null);

  const translations = {
    en: {
      welcome: "Hello! How can I assist you today? You can type 'Product' to see our product list, 'Contact' to get our details, 'Enquiry' to submit a request, 'Catalog' to download our catalog, or 'Support' to speak with an agent.",
      product: "Our Products",
      contact: "Contact Information",
      enquiry: "Submit Enquiry",
      support: "Support Options",
      catalog: "Download Catalog",
      thankYou: "Thank you for your enquiry! Our team will reach out to you shortly.",
      supportHours: "Support Hours: Monday - Friday, 9 AM - 6 PM EST"
    },
    es: {
      welcome: "¡Hola! ¿Cómo puedo ayudarte hoy? Escribe 'Producto' para ver nuestra lista de productos, 'Contacto' para obtener nuestros detalles, 'Consulta' para enviar una solicitud, 'Catálogo' para descargar nuestro catálogo o 'Soporte' para hablar con un agente.",
      // Add other Spanish translations
    }
  };

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  };

  useEffect(() => {
    // Detect user's language
    const userLang = navigator.language.split('-')[0];
    if (translations[userLang]) {
      setLanguage(userLang);
    }

    // Initial welcome message
    setMessages([{
      type: 'bot',
      content: translations[language].welcome
    }]);
  }, []);

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  const handleInputChange = (e) => {
    setInputValue(e.target.value);
  };

  const handleFormChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const userMessage = inputValue.trim().toLowerCase();
    
    if (!userMessage) return;

    setMessages(prev => [...prev, { type: 'user', content: inputValue }]);
    setInputValue('');

    switch (userMessage) {
      case 'product':
        handleProductRequest();
        break;
      case 'contact':
        handleContactRequest();
        break;
      case 'enquiry':
        setShowEnquiryForm(true);
        break;
      case 'catalog':
        handleCatalogRequest();
        break;
      case 'support':
        handleSupportRequest();
        break;
      default:
        setMessages(prev => [...prev, {
          type: 'bot',
          content: translations[language].welcome
        }]);
    }
  };

  const products = [
    {
      name: 'Special Purpose Single Chute',
      description: 'Special Purpose Single Chute Color Sorting Machine',
      price: '20,000',
      image: 'single-chute.jpg'
    },
    {
      name: 'Multi Purpose Four Chute',
      description: 'Multi Purpose Four Chute Color Sorting Solution',
      price: '2,22,222',
      image: 'four-chute.jpg'
    },
    {
      name: 'Multi Purpose Five Chute Machine',
      description: 'Advanced Five Chute Sorting System',
      price: '3,33,333',
      image: 'five-chute.jpg'
    },
    {
      name: 'CONVEYOR TYPE MACHINE',
      description: 'Industrial Conveyor Sorting System',
      price: '4,44,444',
      image: 'conveyor.jpg'
    }
  ];

  const handleProductRequest = () => {
    setShowProducts(true);
    setMessages(prev => [...prev, {
      type: 'bot',
      content: (
        <div className="space-y-4 animate-fade-in">
          <h3 className="font-bold text-light-primary dark:text-dark-primary">{translations[language].product}:</h3>
          <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
            {products.map((product, index) => (
              <div 
                key={index} 
                className="bg-light-card dark:bg-dark-card p-4 rounded-lg border border-light-border dark:border-dark-border hover:shadow-glow transition-shadow animate-slide-up"
                style={{ animationDelay: `${index * 100}ms` }}
              >
                <h4 className="text-lg font-semibold text-light-primary dark:text-dark-primary">{product.name}</h4>
                <p className="text-sm text-light-muted dark:text-dark-muted mt-2">{product.description}</p>
                <div className="flex items-center justify-between mt-4">
                  <span className="text-light-primary dark:text-dark-primary font-mono">₹{product.price}</span>
                  <button className="text-sm text-light-primary dark:text-dark-primary hover:underline">
                    Learn More
                  </button>
                </div>
              </div>
            ))}
          </div>
        </div>
      )
    }]);
  };

  const handleContactRequest = () => {
    setShowProducts(false);
    setMessages(prev => [...prev, {
      type: 'bot',
      content: (
        <div className="space-y-4 animate-fade-in">
          <h3 className="font-bold">{translations[language].contact}:</h3>
          <div className="space-y-2">
            <p className="flex items-center gap-2">
              <FiPhone className="text-light-primary dark:text-dark-primary" />
              <span>+1 234 567 890</span>
            </p>
            <p className="flex items-center gap-2">
              <FiMail className="text-light-primary dark:text-dark-primary" />
              <span>support@example.com</span>
            </p>
            <p className="flex items-center gap-2">
              <FiMapPin className="text-light-primary dark:text-dark-primary" />
              <span>123 Business Street, City, Country</span>
            </p>
            <button className="mt-4 bg-light-primary dark:bg-dark-primary text-white px-4 py-2 rounded-lg flex items-center gap-2 hover:opacity-90 transition-opacity">
              <FiPhone /> Call Us
            </button>
          </div>
        </div>
      )
    }]);
  };

  const handleCatalogRequest = () => {
    setShowProducts(false);
    setMessages(prev => [...prev, {
      type: 'bot',
      content: (
        <div className="space-y-4 animate-fade-in">
          <h3 className="font-bold">{translations[language].catalog}</h3>
          <button 
            className="flex items-center gap-2 bg-light-primary dark:bg-dark-primary text-white px-4 py-2 rounded-lg hover:opacity-90 transition-opacity"
            onClick={() => window.open('/catalog.pdf', '_blank')}
          >
            <FiDownload />
            Download Now
          </button>
        </div>
      )
    }]);
  };

  const handleSupportRequest = () => {
    setShowProducts(false);
    setMessages(prev => [...prev, {
      type: 'bot',
      content: (
        <div className="space-y-4 animate-fade-in">
          <h3 className="font-bold">{translations[language].support}</h3>
          <p className="flex items-center gap-2">
            <FiClock />
            {translations[language].supportHours}
          </p>
          <div className="grid grid-cols-2 gap-4">
            <button className="flex items-center justify-center gap-2 bg-light-primary dark:bg-dark-primary text-white px-4 py-2 rounded-lg hover:opacity-90 transition-opacity">
              <FiMessageSquare />
              Live Chat
            </button>
            <button className="flex items-center justify-center gap-2 bg-green-600 text-white px-4 py-2 rounded-lg hover:opacity-90 transition-opacity">
              <FaWhatsapp />
              WhatsApp
            </button>
          </div>
        </div>
      )
    }]);
  };

  const handleEnquirySubmit = async (e) => {
    e.preventDefault();
    
    try {
      // Here you would typically send the form data to an API
      // const response = await axios.post('/api/enquiry', formData);
      
      setShowEnquiryForm(false);
      setFormData({ name: '', email: '', phone: '', message: '' });
      
      setMessages(prev => [...prev, {
        type: 'bot',
        content: translations[language].thankYou
      }]);
    } catch (error) {
      console.error('Error submitting enquiry:', error);
    }
  };

  return (
    <>
      {/* Chat Toggle Button */}
      <button
        onClick={() => setIsOpen(!isOpen)}
        className={`
          fixed bottom-6 right-6 w-16 h-16
          bg-light-background dark:bg-dark-background
          text-light-primary dark:text-dark-primary
          rounded-full
          flex items-center justify-center
          hover:scale-110 active:scale-95
          transition-all duration-200
          z-50
          shadow-neon hover:shadow-neon-lg
          animate-pulse-neon
          border border-light-primary dark:border-dark-primary
        `}
        aria-label="Toggle chat"
      >
        <FiMessageCircle size={24} className="animate-glow" />
      </button>

      {/* Chat Window */}
      <div
        className={`
          fixed inset-0
          bg-light-background dark:bg-dark-background
          bg-grid-pattern bg-grid
          flex flex-col
          transform transition-all duration-300
          ${isOpen ? 'opacity-100' : 'opacity-0 pointer-events-none'}
          z-40
        `}
      >
        {/* Header */}
        <div className="flex items-center justify-between p-4 bg-light-card dark:bg-dark-card border-b border-light-border dark:border-dark-border">
          <div className="flex items-center gap-2">
            <img src="/swansorter-logo.png" alt="SwanSorter" className="h-8 w-8" />
            <h2 className="font-semibold text-xl text-light-primary dark:text-dark-primary">
              SwanSorter Assistant
            </h2>
          </div>
          <div className="flex items-center gap-4">
            <div className="flex items-center gap-2">
              <span className="text-light-muted dark:text-dark-muted">us</span>
              <button
                onClick={() => setLanguage(language === 'en' ? 'es' : 'en')}
                className="flex items-center gap-1 px-2 py-1 rounded bg-light-secondary dark:bg-dark-secondary text-light-primary dark:text-dark-primary"
              >
                <span>{language.toUpperCase()}</span>
                <FiChevronDown className="w-4 h-4" />
              </button>
            </div>
            <button
              onClick={() => setIsLargeMode(!isLargeMode)}
              className="p-2 rounded hover:bg-light-secondary dark:hover:bg-dark-secondary"
            >
              <FiMaximize2 className="w-5 h-5 text-light-primary dark:text-dark-primary" />
            </button>
            <button
              onClick={() => setIsOpen(false)}
              className="p-2 rounded hover:bg-light-secondary dark:hover:bg-dark-secondary"
            >
              <FiX className="w-5 h-5 text-light-primary dark:text-dark-primary" />
            </button>
          </div>
        </div>

        {/* Messages Container */}
        <div className="flex-1 overflow-y-auto p-4 space-y-4">
          {messages.map((message, index) => (
            <div
              key={index}
              className={`flex ${message.type === 'user' ? 'justify-end' : 'justify-start'}`}
            >
              <div
                className={`
                  max-w-[80%] p-4 rounded-lg animate-slide-up
                  ${message.type === 'user'
                    ? 'bg-light-primary dark:bg-dark-primary text-light-background dark:text-dark-background'
                    : 'bg-light-secondary dark:bg-dark-secondary text-light-primary dark:text-dark-primary border border-light-border dark:border-dark-border'
                  }
                `}
                style={{ animationDelay: `${index * 100}ms` }}
              >
                {typeof message.content === 'string' ? (
                  <div className="space-y-2">
                    <p className="text-sm">{message.content}</p>
                    <p className="text-xs text-light-muted dark:text-dark-muted">
                      {new Date().toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })}
                    </p>
                  </div>
                ) : (
                  message.content
                )}
              </div>
            </div>
          ))}
          <div ref={messagesEndRef} />
        </div>

        {/* Product Grid */}
        {showProducts && (
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-4 p-4">
            {products.map((product, index) => (
              <div
                key={index}
                className="bg-light-card dark:bg-dark-card rounded-lg p-4 border border-light-border dark:border-dark-border hover:shadow-glow transition-shadow"
              >
                <h3 className="text-lg font-semibold text-light-primary dark:text-dark-primary mb-2">
                  {product.name}
                </h3>
                <p className="text-sm text-light-muted dark:text-dark-muted mb-4">
                  {product.description}
                </p>
                <div className="flex items-center justify-between">
                  <span className="text-light-primary dark:text-dark-primary font-mono">
                    ₹{product.price}
                  </span>
                  <button className="text-sm text-light-primary dark:text-dark-primary hover:underline">
                    Learn More
                  </button>
                </div>
              </div>
            ))}
          </div>
        )}

        {/* Input Section */}
        <div className="p-4 border-t border-light-border dark:border-dark-border bg-light-card dark:bg-dark-card">
          <div className="flex gap-2">
            <input
              type="text"
              value={inputValue}
              onChange={handleInputChange}
              placeholder="Type a message..."
              className="flex-1 px-4 py-2 rounded-lg bg-light-input dark:bg-dark-input text-light-primary dark:text-dark-primary placeholder-light-muted dark:placeholder-dark-muted border border-light-border dark:border-dark-border focus:outline-none focus:border-light-primary dark:focus:border-dark-primary"
            />
            <Button
              type="submit"
              variant="default"
              size="icon"
              className="bg-light-primary dark:bg-dark-primary text-light-background dark:text-dark-background"
              onClick={handleSubmit}
            >
              <FiSend className="w-5 h-5" />
            </Button>
          </div>
        </div>
      </div>
    </>
  );
};

export default ChatBot;
